export enum CssTypes {
  stylesheet = 'stylesheet',
  rule = 'rule',
  declaration = 'declaration',
  comment = 'comment',
  charset = 'charset',
  document = 'document',
  customMedia = 'custom-media',
  fontFace = 'font-face',
  host = 'host',
  import = 'import',
  keyframes = 'keyframes',
  keyframe = 'keyframe',
  media = 'media',
  namespace = 'namespace',
  page = 'page',
  supports = 'supports',
